    39→// Load the backgrounds list from both JSON manifests (main + personal)
    40→function loadBundledBackgroundsList() {
    41→  return new Promise((resolve, reject) => {
    42→    const mainJsonUrl = chrome.runtime.getURL("backgrounds/backgrounds.json");
    43→    const personalJsonUrl = chrome.runtime.getURL("backgrounds/personal/backgrounds.json");
    44→
    45→    // Fetch both JSON files in parallel
    46→    Promise.all([
    47→      fetch(mainJsonUrl).then(r => r.ok ? r.json() : []),
    48→      fetch(personalJsonUrl).then(r => r.ok ? r.json() : []).catch(() => [])
    49→    ])
    50→      .then(([mainImages, personalImages]) => {
    51→        // Prepend appropriate paths to each filename
    52→        const mainBgs = mainImages.map(img => "backgrounds/" + img);
    53→        const personalBgs = personalImages.map(img => "backgrounds/personal/" + img);
    54→        bundledBackgrounds = [...mainBgs, ...personalBgs];
    55→        resolve(bundledBackgrounds);
    56→      })
    57→      .catch(error => {
    58→        console.error("Error loading backgrounds.json:", error);
    59→        bundledBackgrounds = [];
    60→        reject(error);
    61→      });
    62→  });
    63→}
